INSERT INTO wagtailcore_pagerevision VALUES(11,0,'{"pk": 9, "path": "0001000100010005", "depth": 4, "numchild": 0, "title": "New state of public emergency declared", "draft_title": "Welcome", "slug": "welcome", "content_type": 27, "live": true, "has_unpublished_changes": true, "url_path": "/home/blog/welcome/", "owner": 1, "seo_title": "", "show_in_menus": true, "search_description": "", "go_live_at": null, "expire_at": null, "expired": false, "locked": false, "first_published_at": "2018-01-30T08:05:53.204Z", "last_published_at": "2020-07-10T04:37:51.027Z", "latest_revision_created_at": "2020-07-10T04:55:44.858Z", "live_revision": 9, "body": "State House, Banjul, 7th July 2020 \u2013 In exercise of the powers conferred on him by section 34(6) of the 1997 Constitution, His Excellency, Adama Barrow, president of the Republic of The Gambia has declared a State of Public Emergency in the whole country. The declaration shall last for a period of seven days, pursuant to section 34(2) with effect from 8th July 2020. \r\n\r\nThe public is reminded that the COVID -19 Emergency Regulations issued under the Emergency Powers Act shall continue to apply during the period of the State of Public Emergency.  \r\n\r\nFurthermore, the public is advised to take all precautionary measures to protect themselves and curb the spread of COVID-19, while the experts continue to monitor and assess the situation.", "date": "2018-01-30T19:00:00Z", "excerpt": "", "header_image": 20, "post_tags": [], "categories": []}',NULL,9,1,'2020-07-10 05:01:46.434413');
INSERT INTO wagtailcore_pagerevision VALUES(12,0,'{"pk": 11, "path": "0001000100010007", "depth": 4, "numchild": 0, "title": "Latex & Code Sample", "draft_title": "Latex & Code Sample", "slug": "latex-code-sample", "content_type": 27, "live": true, "has_unpublished_changes": false, "url_path": "/home/blog/latex-code-sample/", "owner": 1, "seo_title": "", "show_in_menus": false, "search_description": "", "go_live_at": null, "expire_at": null, "expired": false, "locked": false, "first_published_at": "2018-01-30T09:08:58.279Z", "last_published_at": null, "latest_revision_created_at": "2018-01-30T14:17:25.710Z", "live_revision": null, "body": "One of the country\u2019s giant GSM operators, Africell has donated D12,000,000 to Gambia\u2019s effort to contain and defeat the covid-19 pandemic.\r\n\r\nThe funds will be distributed over the next three months directly via Afrimoney to front-line health workers, whose courage and hard work in these difficult circumstances is helping to keep Gambians healthy and safe, according to news release from the GSM operator.\r\n\r\n\u201cThe payments will supplement the normal wages of these front-line workers, giving them the financial security needed to support themselves and their families at a time of significantly increased risk.\u201d\r\n\r\n\u201cAll eligible Doctors will receive D2,000 per month while nurses and all other front-line staff will receive D1,000 per month. The scheme is currently scheduled to last for three months.\u201d\r\n\r\nCommenting on the donation, CEO of Africell Gambia, Alieu Badara Mbye said: \u201cFor Africell, contributing to the campaign against Covid-19 is a duty, not an option. We are stronger together. Africell is committed to standing shoulder-to-shoulder with the government and the people of Gambia as we take on this challenge. Our donation to Gambia\u2019s heroic front-line health workers is a token of our admiration and gratitude to front-line health workers for the risks they take and the hard work they put into keeping the rest of us safe.\u201d\r\n\r\n\u201cTogether we can Break the Chain & Stop the Spread of covid-19. Stay Safe!\u201d", "date": "2018-01-30T09:08:00Z", "excerpt": "Check this page for detail about Latex and Code highlight.", "header_image": 21, "post_tags": [], "categories": []}',NULL,11,1,'2020-07-10 05:05:40.967724');
INSERT INTO wagtailcore_pagerevision VALUES(13,0,'{"pk": 11, "path": "0001000100010007", "depth": 4, "numchild": 0, "title": "Africell donates D12m to fight covid-19", "draft_title": "Latex & Code Sample", "slug": "latex-code-sample", "content_type": 27, "live": true, "has_unpublished_changes": false, "url_path": "/home/blog/latex-code-sample/", "owner": 1, "seo_title": "", "show_in_menus": false, "search_description": "", "go_live_at": null, "expire_at": null, "expired": false, "locked": false, "first_published_at": "2018-01-30T09:08:58.279Z", "last_published_at": "2020-07-10T05:05:41.011Z", "latest_revision_created_at": "2020-07-10T05:05:40.967Z", "live_revision": 12, "body": "One of the country\u2019s giant GSM operators, Africell has donated D12,000,000 to Gambia\u2019s effort to contain and defeat the covid-19 pandemic.\r\n\r\nThe funds will be distributed over the next three months directly via Afrimoney to front-line health workers, whose courage and hard work in these difficult circumstances is helping to keep Gambians healthy and safe, according to news release from the GSM operator.\r\n\r\n\u201cThe payments will supplement the normal wages of these front-line workers, giving them the financial security needed to support themselves and their families at a time of significantly increased risk.\u201d\r\n\r\n\u201cAll eligible Doctors will receive D2,000 per month while nurses and all other front-line staff will receive D1,000 per month. The scheme is currently scheduled to last for three months.\u201d\r\n\r\nCommenting on the donation, CEO of Africell Gambia, Alieu Badara Mbye said: \u201cFor Africell, contributing to the campaign against Covid-19 is a duty, not an option. We are stronger together. Africell is committed to standing shoulder-to-shoulder with the government and the people of Gambia as we take on this challenge. Our donation to Gambia\u2019s heroic front-line health workers is a token of our admiration and gratitude to front-line health workers for the risks they take and the hard work they put into keeping the rest of us safe.\u201d\r\n\r\n\u201cTogether we can Break the Chain & Stop the Spread of covid-19. Stay Safe!\u201d", "date": "2018-01-30T09:08:00Z", "excerpt": "Check this page for detail about Latex and Code highlight.", "header_image": 21, "post_tags": [], "categories": []}',NULL,11,1,'2020-07-10 05:06:08.926990');
